import { useFormik } from "formik";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const AddPost = ({ setPosts,fetchBlogPosts}) => {
  const [fileUpload, setFileUpload] = useState(null);
  const [image, setImage] = useState(null);
  const navigate = useNavigate();
  const formik = useFormik({
    initialValues: {
      title: "",
      excerpt: "",
      content: "",
      category
    },
    onSubmit: (values, { resetForm }) => {
      if (fileUpload) {
        const data = values;

        console.log("FD: ", data);
        postBlog(data);
        resetForm();

      } else {
        alert("Please include an image to upload");
      }
    },
  });

  function postBlog(data) {
    fetch("/blogs", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((resp) => resp.json())
      .then((data) => {
        setPosts(data);
        console.log("DATTA: ", data);
        const id = data.id;
        const formData = new FormData();
        formData.append('file', fileUpload);
        formData.append('description', '');  

        fetch(`/upload/${id}`, {
          method: 'POST',
          body: formData,
        })

          .then((res) => res.json())
          .then((data) => {
            console.log(data);
          });
        navigate("/profile_page");
        fetchBlogPosts()
      });
  }

  const handleChange = (e) => {
    const file = e.target.files[0];
    setFileUpload(file);
    const reader = new FileReader();
    reader.readAsDataURL(file);

    reader.onloadend = () => {
      setImage(reader.result);
      console.log(image);
    };
  };

  return (
    <div>
      <h2>Add Post</h2>

      <form onSubmit={formik.handleSubmit}>
      <div className="filter">

     <select name="filter" value={selectedGenre} onChange={handleChange}>
       <option value="All">Filter By Genre</option>
       <option value="Romance">Romance</option>
       <option value="Fantasy">Fantasy</option>
       <option value="Adventure">Adventure</option>
       <option value="Action">Action</option>
       <option value="Sci-Fi">Sci-Fi</option>
       <option value="Drama">Drama</option>
       <option value="Animation">Animation</option>
       <option value="Crime">Crime</option>
     </select>


 </div>

        <label htmlFor="title">Blog Title</label>
        <br />
        <input
          id="title"
          name="title"
          onChange={formik.handleChange}
          value={formik.values.title}
        />
        <label htmlFor="excerpt">Blog Excerpt</label>
        <br />
        <input
          id="excerpt"
          name="excerpt"
          onChange={formik.handleChange}
          value={formik.values.excerpt}
        />
        <label htmlFor="content">Blog Post</label>
        <br />
        <input
          id="content"
          name="content"
          onChange={formik.handleChange}
          value={formik.values.content}
        />
        <br />
        <label htmlFor="fileInput">Upload Photo Here</label>
        <input
          type="file"
          id="file"
          onChange={(e) => handleChange(e)}
          required
          accept="image/png,image/jpeg,image/jpg,image/jfif"
        />
        <img src={image} alt="" />


        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default AddPost;
