import React from 'react';
import { useFormik } from 'formik';
import { useState } from 'react';
import { retrieve, store } from '../Encryption';
import { useNavigate } from 'react-router-dom';

const UpdateUser = () => {
    const [userData, setUserData] = useState(null);
    const [fileUpload, setFileUpload] = useState(null);
    const [image, setImage] = useState(null);
    const [personalDetails,setPersonalDetails]=useState({
            firstname:"",
            excerpt:"",
            email:"",
    })
    const navigate=useNavigate()
    const formData = new FormData();

    formData.append("file", fileUpload);
    formData.append("description", "");

    const formik = useFormik({
        initialValues: {
            currentPassword:"",
            newPassword: "",
            confirmPassword: "",
        },
        onSubmit: (values, { resetForm }) => {
            postPassword(values);
            resetForm();
        },
    });

    const formik_details = useFormik({
        initialValues: {
            firstname:"",
            lastname:"",
            email:"",
        },
        onSubmit: (values, { resetForm }) => {

            updatePersonalDetails(values);
            resetForm();
        },
    });

    function updatePersonalDetails(data){
        fetch(`/users/${retrieve().user_id}`,{
            method:'PATCH',
            headers:{
                'Content-Type': 'application/json',
                "Authorization":"Bearer "+ retrieve().access_token,      
            },
            body:JSON.stringify(data)
        })
        .then((resp)=>resp.json())
        .then((data)=>{

            store(data)

            navigate('/profile_page')


        })

    }



    function postPassword(data) {
        fetch('/change_password', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                "Authorization":"Bearer "+ retrieve().access_token,                
            },
            body: JSON.stringify(data),
        })
            .then((resp) => resp.json())
            .then((data) => {
                setUserData(data);
            });
    }

    const handleChange = (e) => {
        const file = e.target.files[0];
        setFileUpload(file);
        const reader = new FileReader();
        reader.readAsDataURL(file);

        reader.onloadend = () => {
            setImage(reader.result);
            console.log(reader.result);
        };
    };

    const handlePhotoSubmit = (e) => {
        e.preventDefault();
        fetch('/upload_profileImage', {
            method: 'POST',
            headers:{"Authorization": "Bearer " + retrieve().access_token},
            body: formData,
        })
            .then((resp) => resp.json())
            .then((data) => {
                console.log(data)
                store(data)

                setImage(data);
                navigate('/profile_page')
            });
    };

    return (
        <div >

          <h2>Update User Details</h2>
          <form onSubmit={formik_details.handleSubmit}>
            <div className="form-container">
            <h3>Update Personal Details</h3>
                <div  className="details-container">
                <label htmlFor="firstname">First Name</label>
                <br />
                <input
                  id="firstname"
                  name="firstname"
                  type="text"
                  onChange={formik_details.handleChange}
                  value={formik_details.values.firstname}
                />
                </div>
                <div  className="form-group">

                <label htmlFor="lastname">Last Name</label>
                <br />
                <input
                  id="lastname"
                  name="lastname"
                  type="text"
                  onChange={formik_details.handleChange}
                  value={formik_details.values.lastname}
                />
                </div>
                <div  className="form-group">

                <label htmlFor="email">Email Address</label>
                <br />
                <input
                  id="email"
                  name="email"
                  type="text"
                  onChange={formik_details.handleChange}
                  value={formik_details.values.email}
                />
                </div>
                <div><button type="submit">Update</button></div>

            </div>

          </form>
          <form onSubmit={formik.handleSubmit}>
            <div className="form-container">
              <div className="password-container">
                <h3>Change Password</h3>
                <label htmlFor="currentPassword">Current Password</label>
                <br />
                <input
                  id="currentPassword"
                  name="currentPassword"
                  type="password"
                  onChange={formik.handleChange}
                  value={formik.values.currentPassword}
                />
                <div className="form-group">
                  <label htmlFor="newPassword">New Password</label>
                  <br />
                  <input
                    id="newPassword"
                    name="newPassword"
                    type="password"
                    onChange={formik.handleChange}
                    value={formik.values.newPassword}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="confirmPassword">Confirm Password</label>
                  <br />
                  <input
                    id="confirmPassword"
                    name="confirmPassword"
                    type="password"
                    onChange={formik.handleChange}
                    value={formik.values.confirmPassword}
                  />
                </div>
                <div className="form-group">
                  <button type="submit">Update</button>
                </div>
              </div>
              <div className="profile-container">
                <div className="ui card">
                  <form onSubmit={handlePhotoSubmit}>
                    <div className="form-group">
                      <h6>Upload/Change Profile Image</h6>
                      <label htmlFor="fileInput">Upload Photo Here</label>
                      <input
                        type="file"
                        id="file"
                        onChange={(e) => handleChange(e)}
                        required
                        accept="image/png,image/jpeg,image/jpg,image/jfif"
                      />
                    </div>
                    <button type="submit">Save</button>
                  </form>
                </div>
              </div>
            </div>
          </form>
        </div>
      );

};

export default UpdateUser;
