import * as crypto from "crypto-js"
const secretKey="GsWjudV09e"
function store(userObject){
    const encryptedObject=crypto.AES.encrypt(JSON.stringify(userObject),secretKey).toString()
    localStorage.setItem('jwt',encryptedObject)

}

function retrieve() {
    const decryptObject = localStorage.getItem('jwt');

    if (decryptObject) {
        // Decrypt the data using AES algorithm
        const decryptedData = crypto.AES.decrypt(decryptObject, secretKey);

        // Convert the decrypted data to a base64-encoded string
        const base64String = decryptedData.toString(crypto.enc.Base64);

        return base64String;
    }

    // Return null if there is no data to decrypt
    return null;
}


function remove(){
    if (localStorage.getItem('jwt')){
        localStorage.removeItem('jwt')
    }
}


export {store,retrieve,remove}
