import styled from 'styled-components';

import './theme';
import { TypographyAttrProps, TypographyProps } from './types';
import { typographyMixin } from './typographyMixin';

/** `Typography` is responsible for styling the text according to the designs */
export const Typography = styled.div.attrs((props: TypographyAttrProps) => ({
  'data-test': props['data-test'] ?? 'typography',
}))<TypographyProps>`
  ${typographyMixin};
`;

Typography.defaultProps = {
  size: 'sm',
  type: 'regular',
};
