import React, { useState, useEffect } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import NavBar from './components/NavBar';
import MainPage from './components/MainPage';
import SignUp from './components/SignUp';
import SignIn from './components/SignIn';
import Footer from './components/Footer';
import './App.css';
import ProfilePage from './components/ProfilePage';

function App() {
  const [blogPosts, setBlogPosts] = useState([]);
  const [showSignUp, setShowSignUp] = useState(false);   
  const[showSignIn,setShowSignIn]=useState(false)  
  const[showProfilePage,setShowProfilePage]=useState(false)
  const [user, setUser] = useState(null);
  const [isLoggedIn,setisLoggedIn]=useState(localStorage.get)

  const navigate = useNavigate();

  useEffect(() => {
    fetch('/blogs')
      .then((resp) => resp.json())
      .then((blogs) => {
        setBlogPosts(blogs);
      })
      .catch((error) => {
        console.log('error fetching data', error);
      });
  }, []);


  const handleSignUpClick = () => {
    setShowSignUp(true);
    navigate('/register');
  };
  const handleSignInClick=()=>{
    setShowSignIn(true);
    navigate('/signIn', { replace: true });

  }
  const handleSignOutClick=()=>{
    fetch('/logout')
    .then(()=>{
      setUser(null)
      localStorage.removeItem("user")
      navigate('/',{replace:true})

    })
    .catch((error)=>{
      console.log('error logging out',error)
    })
  }

  return (
    <div className="app">
      <NavBar showSignUp={handleSignUpClick}  showSignIn={handleSignInClick} showSignOut={handleSignOutClick} user={user}/>
      <Routes>
        <Route
          path="/"
          element={
            <>
              <MainPage blogPosts={blogPosts} setBlogPosts={setBlogPosts} />
              <Footer />
            </>
          }
        />
        {showSignUp && (
            <Route

            path="/register"
            element={<SignUp  setShowSignIn={setShowSignIn} />}
          />
        )}
        {showSignIn && (
          <Route 
            path="/signIn"
            element={<SignIn  setShowProfilePage={setShowProfilePage} showProfilePage={showProfilePage} setUser={setUser} user={user} blogPosts={blogPosts}/>}
          />
        )}
        {showProfilePage && (
          <Route
            path="/profile_page"
            element={<ProfilePage  blogPosts={blogPosts}/>}

          />
        )}

      </Routes>
    </div>
  );
}

export default App;
