import React from 'react';



const ProfilePage = ({ location, blogPosts }) => {
  // Check if location and location.state exist
  if (!location || !location.state || !location.state.user) {
    // Redirect to the homepage or handle this situation accordingly
    return <Ma to="/" />;
  }

  const { user } = location.state;
  const blogs = blogPosts.filter((blog) => blog.userId === user.id);

  return (
    <div>
      <div>
        <h2>Welcome, {user.username}!</h2>
        <p>User Details:</p>
        <p>Username: {user.username}</p>
        <p>Email: {user.email}</p>

        <h3>Your Blogs:</h3>
        {blogs.map((blog) => (
          <div key={blog.id}>
            <h4>{blog.title}</h4>
            <p>{blog.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProfilePage;
