import defaultTheme from '@clds/themes';

import { createTheme } from './createTheme';
import { globalThemeId } from '../utils/globalThemeId';

export const globalTheme = createTheme(defaultTheme, globalThemeId);

export type GlobalTheme = ReturnType<typeof globalTheme>;
