import { useFormik } from 'formik';
import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AddPost = ({ addPost }) => {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      title: '',
      excerpt: '',
      content: '',
      images: null, 
    },
    onSubmit: (values, { resetForm }) => {

      const formData = new FormData();
      formData.append('image', values.images);

      fetch('http://localhost:5555/upload', {
        method: 'POST',
        body: formData,
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error('Image upload failed');
          }
          return response.json();
        })
        .then((imageData) => {
          values.images = imageData.file_path;

          // Now, add the post with the image URL
          addPost(values);
          resetForm();
          navigate('/profile_page');
        })
        .catch((error) => {
          console.error('Error uploading image:', error);
        });
    },
  });

  useEffect(() => {
    const cloudinaryWidget = window.cloudinary.createUploadWidget(
      {
        cloudName: 'your-cloud-name',
        uploadPreset: 'your-upload-preset',
        folder: 'optional-folder',
        sources: ['local', 'url', 'camera', 'image_search'],
      },
      (error, result) => {
        if (!error && result && result.event === 'success') {
          formik.setFieldValue('images', result.info.secure_url);
        }
      }
    );

    document.getElementById('upload-button').addEventListener('click', () => {
      cloudinaryWidget.open();
    });
  }, [formik]);

  return (
    <div>
      <h2>Add Post</h2>

      <form onSubmit={formik.handleSubmit}>
        <label htmlFor="title">Blog Title</label>
        <br />
        <input
          id="title"
          name="title"
          onChange={formik.handleChange}
          value={formik.values.title}
        />

        <label htmlFor="excerpt">Blog Excerpt</label>
        <br />
        <input
          id="excerpt"
          name="excerpt"
          onChange={formik.handleChange}
          value={formik.values.excerpt}
        />

        <label htmlFor="content">Blog Post</label>
        <br />
        <input
          id="content"
          name="content"
          onChange={formik.handleChange}
          value={formik.values.content}
        />

        <br />
        <button type="button" id="upload-button">
          Upload Image
        </button>
        <br />

        <input
          type="file"
          id="image"
          name="image"
          onChange={(event) => formik.setFieldValue('images', event.currentTarget.files[0])}
        />
        <br />

        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default AddPost;
