import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom'

const UpdatePost = (updatedPost) => {
    const {id}=useParams()
    useEffect(()=>{
        fetch(`/blogs/${id}`,{
            method:'PATCH',
            headers:{
                "Content-Type":"application/json",
            },
            body:JSON.stringify(updatedPost)
        })
        .then((resp)=>resp.json())
        ,then()
    })
  return (
    <div>


    </div>
  )
}

export default UpdatePost
