/* eslint-disable import/no-anonymous-default-export,import/no-default-export */
import color from './color';

export default {
  size: {
    xxl: '3rem',
    xl: '2.25rem',
    lg: '1.5rem',
    md: '1.25rem',
    sm: '1rem',
    xs: '.875rem',
    xxs: '.75rem',
  },
  color: {
    base: color.gray60,
    primary: color.gray90,
    secondary: color.gray50,
  },
};
