import React, { useState } from 'react'
import { retrieve } from '../Encryption'

const AddCategory = ({setCategories,categories}) => {
    const[formData,setFormData]=useState(null)


    const addCategory=()=>{
        fetch("/categories",{
            method:"POST",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Bearer "+ retrieve().access_token
            },
            body:JSON.stringify(formData)

        })
        .then((resp)=>resp.json())
        .then((data)=>{
            setCategories(...categories,data)

        })
        .catch((error)=>{
            console.log(error)
        })

    }
  return (
    <div>
        <form onSubmit={addCategory}>
        <label htmlFor='new_category'>New Category</label>
        <input type="text" id="new_category" placeholder='Enter New Category'></input>
        <br></br>
        <label htmlFor='new_category_description'>New Category Description</label>
        <input type="text" id="description" placeholder='Enter Category Description'></input>
        <br></br>
        <button type="submit">Save New Category</button>
        </form>


    </div>
  )
}

export default AddCategory
