import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import AddComment from "./AddComment";
import { retrieve } from "../Encryption";

const SingleBlogPage = ({ handleComment }) => {
  const { id } = useParams();
  const [blogPost, setBlogPost] = useState(null);
  const [myComment, setMyComment] = useState(null);
  const [isOpen, setIsOpen] = useState({ id: null, open: false });

  useEffect(() => {
    fetch(`/blogs/${id}`)
      .then((resp) => resp.json())
      .then((blog) => {
        setBlogPost(blog);
      })
      .catch((error) => {
        console.log("error fetching blog post", error);
      });
  }, [id]);

  const handleUpdateComment = (commentId) => {
    fetch(`/comments/${commentId}`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ content: myComment }),
    })
      .then((res) => res.json())
      .then((data) => {
        setIsOpen({ id: commentId, open: false });
        console.log(data);
      })
      .catch((err) => {
        throw new Error(err);
      });
  };

  if (!blogPost) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>{blogPost.title}</h2>

      <div className="image-container">
        {blogPost.images.map((image, index) => (
          <img
            key={index}
            className="blog-image"
            src={image.file_path}
            alt={`${index + 1}`}
          />
        ))}
      </div>

      <div>
        <p>{blogPost.content}</p>
      </div>

      <div>
        <h2>Post Comments</h2>
        {blogPost.comments && blogPost.comments.length >= 1 ? (
          blogPost.comments.map((comment, index) => (
            <div key={index}>
              <p>{comment.guest_name}</p>
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "5px",
                  justifyContent: "center",
                }}
              >
                <p>{comment.content}</p>
                {comment.user_id === retrieve().user_id ? (
                  <button onClick={() => setIsOpen((val) => {id: comment.id, open})}>
                    Update
                  </button>
                ) : (
                  ""
                )}
                {isOpen ? (
                  <div>
                    <input
                      id="newComment"
                      name="newComment"
                      value={myComment}
                      onChange={(e) => setMyComment(e.target.value)}
                    />
                    <button onClick={() => handleUpdateComment(comment.id)}>
                      Save
                    </button>
                  </div>
                ) : (
                  ""
                )}
              </div>
            </div>
          ))
        ) : (
          <p>No Post Comments</p>
        )}
      </div>

      <div>
        <AddComment handleComment={handleComment} blogPost={blogPost} />
      </div>
    </div>
  );
};

export default SingleBlogPage;
