import React from 'react'
import { useState } from 'react'
import { useParams } from 'react-router-dom'

const UpdatePost = ({blogPosts,setBlogPosts}) => {
    const{id}=useParams()
    const[updatedPost,setUpdatedPost]=useState(null)
    const handleEditClick = (postId) => {
        const updatePosts = blogPosts.map((post) =>
            post.id === postId ? { ...post, ...updatedPost } : post
        );

        fetch(`/blogs/${id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updatePosts.find((post) => post.id === postId)),
        })
            .then((resp) => resp.json())
            .then(() => {
                setBlogPosts(updatePosts);
                setUpdatedPost({
                    title: "",
                    excerpt: "",
                    content: "",
                });
            })
            .catch((error) => {
                console.error("Error updating post:", error);
            });
    };


  return (
    <div className='edit_post'>
        <label>Title</label>
      <input
      id="title"
      name="title"
      type="text"
      value={updatedPost.title || ""}
      onChange={(e)=>setUpdatedPost({...updatedPost,title:e.target.value})}
      />
      <input
      id="excerpt"
      name="excerpt"
      type="text"
      value={updatedPost.excerpt || " "}
      onChange={(e)=>setUpdatedPost({...updatedPost,excerpt:e.target.value})}
      />
     <input
      id="content"
      name="content"
      type="text"
      value={updatedPost.content || " "}
      onChange={(e)=>setUpdatedPost({...updatedPost,content:e.target.value})}
      />
      <button onClick={handleEditClick}>Save</button>

    </div>
  )
}

export default UpdatePost
