const UpdatePost = ({ blogPosts, setBlogPosts }) => {
    const { id } = useParams();
    const [updatedPost, setUpdatedPost] = useState({});

    // Function to update the updatedPost state
    const handleUpdatePost = (newData) => {
      setUpdatedPost((prevData) => ({ ...prevData, ...newData }));
    };

    useEffect(() => {
      const updatedPosts = blogPosts.map((post) =>
        post.id === id ? { ...post, ...updatedPost } : post
      );

      fetch(`/blogs/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedPost),
      })
        .then((resp) => resp.json())
        .then(() => {
          setBlogPosts(updatedPosts);
        });
    }, [id, blogPosts, setBlogPosts, updatedPost]);

    return <div></div>;
  };

  export default UpdatePost;
