import React from 'react';
import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const UpdatePost = ({ post }) => {
    const { id } = useParams();
    const [updatedPost, setUpdatedPost] = useState({
        title: post.title,
        excerpt: post.excerpt,
        content: post.content,
    });

    const handleEditClick = () => {
        const postId = id; 
        console.log(postId)
        const loggedInUserId = parseInt(localStorage.getItem("user").id);
        const post = blogPosts.find((post) => post.user_id === loggedInUserId);

        if (!post) {
            console.error("Post not found");
            return;
        }




        fetch(`/blogs/${postId}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(post),
        })
        .then((resp) => resp.json())
        .then(() => {
            const updatedPosts = [...blogPosts];
            updatedPosts[updatedPostIndex] = updatedPostData;
            setBlogPosts(updatedPosts);
            setUpdatedPost({
                title: "",
                excerpt: "",
                content: "",
            });
        })
        .catch((error) => {
            console.error("Error updating post:", error);
        });
    };



    return (
        <div className='edit_post'>
            <label>Title</label>
            <input
                id="title"
                name="title"
                type="text"
                value={updatedPost.title}
                onChange={(e) => setUpdatedPost({ ...updatedPost, title: e.target.value })}
            />
             <label>Excerpt</label>
            <input
                id="excerpt"
                name="excerpt"
                type="text"
                value={updatedPost.excerpt}
                onChange={(e) => setUpdatedPost({ ...updatedPost, excerpt: e.target.value })}
            />
             <label>Content</label>
            <input
                id="content"
                name="content"
                type="text"
                value={updatedPost.content}
                onChange={(e) => setUpdatedPost({ ...updatedPost, content: e.target.value })}
            />
            <button onClick={()=>handleEditClick()}>Save</button>
        </div>
    );
};

export default UpdatePost;
