export type BlendVariant =
  | 'primary'
  | 'secondary'
  | 'error'
  | 'success'
  | 'warning'
  | 'overlay';

export type BlendMode =
  | 'solidContrast'
  | 'plainInvert'
  | 'surfaceSolid'
  | 'surfaceContrast'
  | 'plainSolid'
  | 'plainContrast';
export type BlendIntensity = 'sm' | 'md' | 'lg' | 'xl';

export interface BlenderForegroundProps {
  variant: BlendVariant;
  mode: BlendMode;
}

export interface BlenderBackgroundProps extends BlenderForegroundProps {
  intensity: BlendIntensity;
}

export type BlenderProps = BlenderBackgroundProps & BlenderForegroundProps;
