import { GlobalTheme } from '@clds/component-theme';
import { TypographyType } from '@clds/typography';

export type OpaqueColorName = Exclude<
  keyof GlobalTheme['palette'],
  'dataVis' | 'code' | 'brand'
>;
export type ColorName = OpaqueColorName | 'transparent';

export interface ForegroundBlendOptions {
  colorName: OpaqueColorName;
  typography: TypographyType;
}

export interface BackgroundBlendOptions {
  baseColor: ColorName;
  blendColor: ColorName;
  blendOpacity: 0 | 0.06 | 0.12 | 0.18;
}
