import { ComponentType, ReactNode } from 'react';
import { StyledComponentPropsWithRef } from 'styled-components';

/** Interface for props of all UI components. */
export interface CommonComponentProps<
  RootTagName extends keyof JSX.IntrinsicElements | ComponentType,
> {
  /** Props passed directly to the root element rendered in this component. */
  rootProps?: StyledComponentPropsWithRef<RootTagName>;
  children?: ReactNode;
}
