import { assert } from 'chai';

import { combineValues } from '../../src/api/combineValues';

describe('combineValues', () => {
  it('combines values', () => {
    const getNumber = () => 5 * 3;
    const getString = () => 'foo bar';
    const getStruct = () => ({ name: 'Adam', lastName: 'Borowski' });

    assert.strictEqual(
      combineValues(getString, getNumber, getStruct, (a, b, c) =>
        [a.charAt(0), b.toFixed(2), c.name, c.lastName].join(', '),
      )({ theme: {} }),
      'f, 15.00, Adam, Borowski',
    );
  });
});
