import { assert } from 'chai';

// eslint-disable-next-line @nx/enforce-module-boundaries
import defaultTheme from '@clds/themes';

import { globalTheme } from '../../src/api/globalTheme';

describe('globalTheme', () => {
  it('produces palette mixins', () => {
    assert.strictEqual(
      globalTheme.palette.primary({ theme: defaultTheme }),
      defaultTheme.palette.primary,
    );
  });
  it('produces correct toString function', () => {
    assert.strictEqual(
      globalTheme.palette.primary.toString(),
      'mixins.palette.primary',
    );
  });
});
