import { assert } from 'chai';

import { isParent } from '../../src/utils/isParent';

describe('isParent', () => {
  // eslint-disable-next-line mocha/no-setup-in-describe
  const mixin = Object.assign(() => 123, { foo: 'bar' });

  const tests = [
    [true, false],
    [false, false],
    [213, false],
    ['123', false],
    [() => 234, false],
    [{ foo: 123 }, true],
    [mixin, true],
  ];

  for (const [input, result] of tests) {
    it(`input=${input.toString()}, isParent=${result.toString()}`, () => {
      assert.strictEqual(isParent(input), result);
    });
  }
});
