import { assert } from 'chai';
import React from 'react';
import 'global-jsdom/register';

import { render } from '@clds/testing-library';

import { createIcon } from './createIcon';

describe('createIcon', () => {
  beforeEach(() => {});

  it('should create icon and add it to the descriptor', () => {
    const Icon = createIcon('fake path');

    const screen = render(<Icon />);

    const pathElement = screen.getByTestId('icon').querySelector('path');
    assert.ok(pathElement);
    assert.equal(pathElement?.getAttribute('d'), 'fake path');
  });
});
