import { OpaqueColorName } from '@clds/blender';

import { theme } from './theme';

export type IconSize = keyof ReturnType<typeof theme>['bySize'];

/** Interface to put optional defaults for an icon.
 *  Useful when you have compound components, like button, that will specify context for Icon for easy of use
 * */

export interface IconProps {
  color?: string | OpaqueColorName;
  size?: IconSize;
}
