import { assert } from 'chai';

import defaultTheme, { dark as darkTheme } from '../src';

describe('Themes', () => {
  describe('default.theme', () => {
    it('global params should exist', () => {
      assert.ok(defaultTheme.radius);
      assert.ok(defaultTheme.spacing);
      assert.ok(defaultTheme.palette);
      assert.ok(defaultTheme.shadow);
      assert.ok(defaultTheme.globals);
    });
  });

  describe('dark tests', () => {
    it('overridden params should exist', () => {
      assert.ok(darkTheme.palette);
    });
  });
});
