import { ReactNode } from 'react';

export type TypographySize = 'xxl' | 'xl' | 'lg' | 'md' | 'sm' | 'xs' | 'xxs';
export type TypographyType =
  | 'highContrast'
  | 'regular'
  | 'lowContrast'
  | 'disabled'
  | 'url'
  | 'success'
  | 'warning'
  | 'error'
  | 'contrastInvert'
  | 'overlay'
  | 'overlayInvert';

export interface TypographyProps {
  size: TypographySize;
  type: TypographyType;
  children?: ReactNode;
}

export interface TypographyAttrProps {
  'data-test'?: string | undefined;
}
